// proxy api requests
const exampleProxy = proxy(options); // \u8FD9\u91CC\u7684 options \u5C31\u662F webpack \u91CC\u9762\u7684 proxy \u9009\u9879\u5BF9\u5E94\u7684\u6BCF\u4E2A\u9009\u9879

// mount \`exampleProxy\` in web server
