  mergeSort(2, 3) // mid = 2
    mergeSort(3, 3) // \u9047\u5230\u7EC8\u6B62\uFF0C\u56DE\u9000\u5230\u4E0A\u4E00\u6B65
  // \u6392\u5E8F p1 = 2, p2 = mid + 1 = 3
  // \u56DE\u9000\u5230 \`mergeSort(data, 0, 3)\` \u6267\u884C\u5408\u5E76\u903B\u8F91
  // \u6392\u5E8F p1 = 0, p2 = mid + 1 = 2
